import React from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Landing from "./pages/landingPage/Landing";
import About from "./pages/aboutPage/About";
import Login from "./pages/loginPage/Login";
import SignUp from "./pages/signUpPage/SignUp";
import MyMissions from "./pages/myMissionsPage/MyMissions";
import MyProfile from "./pages/myProfilePage/MyProfile";
import Mission from "./pages/missionPage/Mission";

import netlifyIdentity from "netlify-identity-widget";
import auth from "./hoc/AuthHOC";
import { loginUser, logoutUser } from "../src/utils/identityActions";

import "./App.css";

React.useEffect = () => {
  const user = localStorage.getItem("currentOpenSaucedUser");
  if (user) {
    this.setState({ user: JSON.parse(user) });
  } else {
    loginUser();
  }
  netlifyIdentity.on("login", (user) => this.setState({ user }, loginUser()));
  netlifyIdentity.on("logout", (user) =>
    this.setState({ user: null }, logoutUser()),
  );
};

const App = () => {
  return (
    <div className="App">
      <Router>
        <Switch>
          <Route exact path="/" component={() => <Landing />} />
          <Route path="/about" component={() => <About />} />
          <Route path="/signup" component={() => <SignUp />} />
          <Route path="/login" component={() => <Login />} />
          <Route path="/my-missions" component={() => <MyMissions />} />
          <Route path="/my-profile" component={() => <MyProfile />} />
          <Route path="/mission/:number" component={() => <Mission />} />
        </Switch>
      </Router>
    </div>
  );
};

export default App;
